INSERT INTO `groups` (`id`, `group_name`, `permission`, `created_at`, `updated_at`) VALUES
(1,	'Administrator',	'a:24:{i:0;s:10:\"createUser\";i:1;s:10:\"updateUser\";i:2;s:8:\"viewUser\";i:3;s:10:\"deleteUser\";i:4;s:13:\"createReports\";i:5;s:13:\"updateReports\";i:6;s:11:\"viewReports\";i:7;s:13:\"deleteReports\";i:8;s:11:\"createBlogs\";i:9;s:11:\"updateBlogs\";i:10;s:9:\"viewBlogs\";i:11;s:11:\"deleteBlogs\";i:12;s:18:\"createPressRelease\";i:13;s:18:\"updatePressRelease\";i:14;s:16:\"viewPressRelease\";i:15;s:18:\"deletePressRelease\";i:16;s:11:\"createGroup\";i:17;s:11:\"updateGroup\";i:18;s:9:\"viewGroup\";i:19;s:11:\"deleteGroup\";i:20;s:14:\"createCategory\";i:21;s:14:\"updateCategory\";i:22;s:12:\"viewCategory\";i:23;s:14:\"deleteCategory\";}',	'2020-07-08 09:37:12',	'2021-03-08 18:16:10'),
